raw_recipe:
{%set name = "cloudant" %}
{%set version = "0.5.10" %}
{%set compress_type = "tar.gz" %}
{%set hash_type = "sha256" %}
{%set hash_val = "ed79234cd796c777a10ebf02287af3deb8739c594e66a1d012a2955161942b31" %}
{%set build_num = "0"%}

package:
  name: {{ name|lower }}
  version: {{ version }}

source:
  fn: {{ name }}-{{ version }}.{{ compress_type }}
  url: https://pypi.io/packages/source/{{ name[0] }}/{{ name }}/{{ name }}-{{ version }}.{{ compress_type }}
  {{ hash_type }}: {{ hash_val }}

build:
  skip: True  # [py3k]
  number: {{ build_num }}
  script: python setup.py install --single-version-externally-managed --record=record.txt


requirements:
  build:
    - python
    - setuptools

  run:
    - python
    - requests-futures ==0.9.4

test:
  imports:
    - cloudant

about:
  home: https://cloudant-labs.github.io/cloudant-python/
  license: MIT
  # license_file: No MANIFEST.in or LICENSE
  license_family: MIT
  summary: 'Asynchronous Cloudant / CouchDB Interface'
  dev_url: https://github.com/cloudant-labs/cloudant

extra:
  recipe-maintainers:
    - pmlandwehr
